const slider=document.getElementById("products-slider"),left=document.getElementById("left"),right=document.getElementById("right"),featuredProducts=products.filter(p=>!0===p.featured);buildProducts(featuredProducts,slider);const card=document.getElementsByClassName("product-card")[0],width=card.offsetWidth,containerWidth=(featuredProducts.length-1)*width;function carouselScroll(width){slider.scrollBy({left:width,top:0,behavior:"smooth"})}right.addEventListener("click",()=>{carouselScroll(width)}),left.addEventListener("click",()=>{carouselScroll(-width)});let x=0;function animate(){carouselScroll(width),x+=width,containerWidth>window.innerWidth&&x>=containerWidth&&(carouselScroll(-featuredProducts.length*width),x=0),containerWidth<window.innerWidth&&x+containerWidth>window.innerWidth&&(carouselScroll(-featuredProducts.length*width),x=0)}window.setInterval(()=>animate(),3e3);